import { useState, useRef } from "react";

const DD = () => {
    const [files, setFiles] = useState(null);
    const inputRef = useRef();

    const handleDragOver = (event) => {
        event.preventDefault();
    };

    const handleDrop = (event) => {
        event.preventDefault();
        setFiles(event.dataTransfer.files)
    };

    const openInNewTab = () => {
        const url = URL.createObjectURL(files);
        window.open(url, '_blank');
  };

    /*// send files to the server
    const handleUpload = () => {
        const formData = new FormData();
        formData.append("Files", files);
        console.log(formData.getAll())
        fetch("link", { method: "POST", body: formData })
    };*/

    // Code to send a file to the server in React.js
    const handleUpload = async (files) => {
        const formData = new FormData();
        formData.append("Files", files);
  
    try {
      const response = await fetch('https://example.com/upload', {
        method: "POST",
        body: formData,
      });
  
      if (response.ok) {
        console.log('File uploaded successfully');
      } else {
        console.error('Failed to upload file');
      }
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };
  

    if (files) return (
        <div className="uploads">
            <div className="actions">
                <p>ARE YOU SURE YOU WANT TO UPLOAD THIS FILE</p>
                <button onClick={() => setFiles(null)}>Cancel</button>
                <button onClick={handleUpload}>Upload</button>
            </div>
        </div>
    )

    return (
        <>
            <div
                className="dropzone"
                onDragOver={handleDragOver}
                onDrop={handleDrop}
            >
                <h2 style={{ marginBottom: '10px' }}>Drag and Drop Files to Upload</h2>
                <h3 style={{ marginTop: '0.5px' }}>Or</h3>
                <input
                    type="file"
                    multiple
                    onChange={(event) => setFiles(event.target.files)}
                    hidden
                    accept="image/png, image/jpeg"
                    ref={inputRef}
                />
                <button onClick={(openInNewTab) => inputRef.current.click()}>Select Files</button>
            </div>
        </>
    );
};

export default DD;
